#!/usr/bin/env perl
#Cargo culting like a boss from the autogenerated catalyst creator
use inc::Module::Install 1.02;

main( @ARGV );

sub main {
	my ( $sets ) = @_;

	my @setstring =;
	my $settings  = {};
	for my $option ( split( ',', $sets ) ) {
		for my $supported ( qw/full excel/ ) {
			if ( $option eq $supported ) {
				$settings->{$supported} = 1;
			}
		}
	}

	#Install whichever version available
	for my $module (
		qw/
		DBI
		Try::Tiny
		Moo
		Digest::MD5
		Text::CSV
		App::RewriteVersion
		JSON
		File::Find::Rule
		YAML
		App::whichpm
		Log::Log4perl
		/
	  )
	{
		requires $module;

	}

	if ( $option->{excel} || $option->{full} ) {

		#Install whichever version available
		for my $module (
			qw/
			Excel::Writer::XLSX
			/
		  )
		{
			requires $module;

		}
	}

	# *may* be the better way to transpose pathscripts to /usr/local/bin
	# install_script glob( 'script/*.pl' );

	auto_install;
	WriteAll;

}
